body.script:
//

// Shopping Cart System
let cart = JSON.parse(localStorage.getItem('gosmartCart')) || [];

function updateCartCount() {
    document.getElementById('cartCount').textContent = cart.length;
}

function addToCart(title, image, button) {
    // Get price from the product card
    const productCard = button.closest('.product-card');
    const priceElement = productCard.querySelector('.product-price');
    const price = priceElement ? priceElement.textContent.trim() : 'Rp0';

    // Check if item already in cart
    const existingItem = cart.find(item => item.title === title);
    if (existingItem) {
        alert('Produk sudah ada di keranjang!');
        return;
    }

    // Add to cart
    cart.push({
        title: title,
        image: image,
        price: price,
        id: Date.now()
    });

    // Save to localStorage
    localStorage.setItem('gosmartCart', JSON.stringify(cart));

    // Update UI
    updateCartCount();
    updateCartDisplay();

    // Show feedback
    button.textContent = 'Ditambahkan âœ“';
    button.style.background = '#51cf66';
    setTimeout(() => {
        button.textContent = 'Tambah ke Keranjang';
        button.style.background = '';
    }, 2000);
}

function removeFromCart(id) {
    cart = cart.filter(item => item.id !== id);
    localStorage.setItem('gosmartCart', JSON.stringify(cart));
    updateCartCount();
    updateCartDisplay();
}

function toggleCart() {
    const modal = document.getElementById('cartModal');
    modal.classList.toggle('active');
    updateCartDisplay();
}

function updateCartDisplay() {
    const cartItems = document.getElementById('cartItems');
    const cartFooter = document.getElementById('cartFooter');
    const cartTotal = document.getElementById('cartTotal');

    if (cart.length === 0) {
        cartItems.innerHTML = '<div class="cart-empty">Keranjang belanja Anda kosong</div>';
        cartFooter.style.display = 'none';
        return;
    }

    // Calculate total
    let total = 0;
    cart.forEach(item => {
        const priceNum = parseInt(item.price.replace(/[^0-9]/g, ''));
        if (!isNaN(priceNum)) {
            total += priceNum;
        }
    });

    // Display items
    let html = '';
    cart.forEach(item => {
        html += `
            <div class="cart-item">
                <img src="${item.image}" alt="${item.title}" class="cart-item-image">
                <div class="cart-item-info">
                    <div class="cart-item-title">${item.title}</div>
                    <div class="cart-item-price">${item.price}</div>
                </div>
                <button class="remove-item" onclick="removeFromCart(${item.id})">Hapus</button>
            </div>
        `;
    });

    cartItems.innerHTML = html;
    cartTotal.textContent = 'Rp' + total.toLocaleString('id-ID');
    cartFooter.style.display = 'block';
}

function goToCheckout() {
    // Save cart to localStorage for checkout page
    localStorage.setItem('gosmartCart', JSON.stringify(cart));
    // Redirect to checkout page
    window.location.href = '/p/checkout.html';
}

// Initialize on page load
updateCartCount();

// Close modal when clicking outside
document.getElementById('cartModal').addEventListener('click', function(e) {
    if (e.target === this) {
        toggleCart();
    }
});

